import React, { useState, useEffect, useRef } from "react";
import css from "./PhotoSession.module.css"; // Ensure that this path matches your CSS module
import Timer from "../../components/Timer/Timer";

const PhotoSession = () => {
  const [countDown, setCountDown] = useState(5);

  useEffect(() => {
    if (countDown === 0) return;
    const intervalId = setInterval(() => {
      setCountDown((prevCountDown) => prevCountDown - 1);
    }, 1000);
    return () => clearInterval(intervalId);
  }, [countDown]);

  const takePicture = () => {
    const width = 500; 
    const height = width / (16 / 9); 

    // Creating a new canvas element dynamically
    const newCanvas = document.createElement("canvas");
    newCanvas.width = width;
    newCanvas.height = height;
    const ctx = newCanvas.getContext("2d");
    if (ctx && videoRef.current) {
      ctx.drawImage(videoRef.current, 0, 0, width, height);
      canvasRefs.current.push(newCanvas); // Store the canvas in the refs array
    }
    updateDisplay();
  };

  const updateDisplay = () => {
    const container = document.getElementById("canvasContainer");
    container.innerHTML = ""; // Clear existing canvases displayed
    canvasRefs.current.forEach((canvas) => {
      container.appendChild(canvas); // Append each canvas to the container
    });
  };


  return (
    <div className={css.container}>
      <Timer countDown={countDown} />
      <div id="canvasContainer"></div> 
    </div>
  );
};

export default PhotoSession;
