import React, { useEffect } from "react";
import css from "./Print.module.css";
import { useAppContext } from "../../AppContext";
import html2canvas from "html2canvas";

const Print = () => {
  const { canvasRefs, frameRef, filterRef } = useAppContext();

  useEffect(() => {
    // Create a reference to the frame element
    const frameElement = document.querySelector(`.${css.frame}`);

    // Apply filter to each photo before printing
    const photos = frameElement.querySelectorAll(`.${css.photo}`);
    photos.forEach((photo) => {
      photo.style.filter = filterRef.current;
    });

    // Use html2canvas to create a canvas from the frame element
    html2canvas(frameElement).then((canvas) => {
      // Convert the canvas to a data URL
      const imgUrl = canvas.toDataURL("image/png");

      // Create a temporary link element
      const link = document.createElement("a");
      link.href = imgUrl;
      link.download = "photo.png";

      // Append the link to the body and click it to trigger the download
      document.body.appendChild(link);
      link.click();

      // Clean up by removing the temporary link element
      document.body.removeChild(link);

      // Reset filter styles after printing
      photos.forEach((photo) => {
        photo.style.filter = "";
      });
    });
  }, []);

  return (
    <div className={css.container}>
      <div className={css.leftSide}>
        <div className={css.frameContainer}>
          <img src="/images/select_filter/leather_frame.svg" alt="" className={css.leatherFrame} />
          <div className={css.frame}>
            {canvasRefs.current.map((canvas, index) =>
              index === 2 ? (
                <>
                  <div className={css.picture}>
                    <img src="/images/select_filter/piper_logo.svg" alt="Static Image" />
                  </div>
                  <div className={css.photo} style={{ filter: filterRef.current }}>
                    {canvas && <canvas ref={(el) => el && el.replaceWith(canvas)}></canvas>}
                  </div>
                </>
              ) : (
                <div key={index} className={css.photo} style={{ filter: filterRef.current }}>
                  {canvas && <canvas ref={(el) => el && el.replaceWith(canvas)}></canvas>}
                </div>
              )
            )}
          </div>
        </div>
      </div>
      <div className={css.rightSide}>
        <div className={css.printContainer}>
          <div className={css.title}>
            <h2>Printing Your Memories</h2>
          </div>
          <div className={css.printImg}>
            <img src="/images/print/print.svg" alt="" />
          </div>
          <div className={css.bottomText}>
            <h4>Creating another memories in (60s)</h4>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Print;
