import React, { useState } from "react";
import css from "./SelectFrame.module.css";
import Frame from "../../components/Frame/Frame";

const SelectFrame = () => {
  const colorArray = ["pink", "blue", "green", "yellow"];

  const [selectedColor, setSelectedColor] = useState(""); // State to hold the selected color

  const handleSelectColor = (color) => {
    setSelectedColor(color); // Update the selected color
  };
  return (
    <div className={css.container}>
      <div className={css.frameContainer}>
        <img src="/images/select_frame/frame_bg.svg" alt="" />
        <div
          className={css.imgPlaceholder}
          style={{ 
            backgroundColor: selectedColor || '#f0f0f0',  // Fallback to default gray if no color selected
            backgroundImage: selectedColor ? 'none' : "url('../../../public/images/select_frame/empty_frame.svg')"
          }}
        ></div>
        <div className={css.frameLogo}>
          <img src="/images/select_frame/select_frame.svg" alt="" />
        </div>
      </div>
      <div className={css.rightContainer}>
        <div className={css.top}>
          <h2>Frames</h2>
        </div>
        <div className={css.bottom}>
          <div className={css.frameWrapper}>
            {colorArray.map((color, index) => (
              <Frame key={index} bgColor={color} onSelectColor={() => handleSelectColor(color)} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SelectFrame;
